    private GUISovellus guiSovellus;
    private KomentoKirjasto komentoKirjasto;
    private JLabel aktiivinen;
    private int aktiivistenMaaraJosUseampi;
    
    private void klikattuSiirra(JLabel korttiLabel){
       if(guiSovellus.onkoKortti(korttiLabel.getName())){
            if(aktiivinen == null){    
                if(guiSovellus.onkoPelipakanPaallimmainen(korttiLabel.getName())){
                    aktiivinen = korttiLabel;
                    korttiLabel.setEnabled(false);
                }
            }
            else {
                //if(aktiivistenMaaraJosUseampi != 0) T√ÑT√Ñ JOUTUU EHK√Ñ K√ÑYTT√ÑM√Ñ√ÑN
                guiSovellus.pushKomento(komentoKirjasto.siirra(aktiivinen.getName(), korttiLabel.getName()));
                paivita();
            }
        }
    }
    private void klikattuSiirraPelipakasta(JLabel korttiLabel){
        if(guiSovellus.onkoKortti(korttiLabel.getName())){
            if(aktiivinen == null){
                if(guiSovellus.onkoPelipakanPaallimmainen(korttiLabel.getName())){
                    aktiivinen = korttiLabel;
                    korttiLabel.setEnabled(false);
                }
                else{
                    aktiivistenMaaraJosUseampi = guiSovellus.montaPelipakassaPaalla(korttiLabel.getName()) +1;
                    aktiivinen = korttiLabel;
                    maalaaAlaspain((JLayeredPane) korttiLabel.getParent(), aktiivistenMaaraJosUseampi);
                }
            }
            else {
                if(aktiivistenMaaraJosUseampi == 0)
                    guiSovellus.pushKomento(komentoKirjasto.siirra(aktiivinen.getName(), korttiLabel.getName()));
                else
                    guiSovellus.pushKomento(komentoKirjasto.siirraMonta(aktiivinen.getName(), korttiLabel.getName(), aktiivistenMaaraJosUseampi));
                paivita();
                
            }
        }
    }
    private void tyhjennaAktiivinen(){
        if(aktiivistenMaaraJosUseampi == 0){
            aktiivinen.setEnabled(true);
            aktiivinen = null;
        }
        else{
            poistaMaalausAlaspain((JLayeredPane) aktiivinen.getParent(), aktiivistenMaaraJosUseampi);
            aktiivinen = null;
            aktiivistenMaaraJosUseampi = 0;
        }
    }
    
    private void maalaaAlaspain(JLayeredPane pane, int i){
        for(int j = pane.getComponentCount() -1; j >= i; j--){
            JLabel korttiLabel = (javax.swing.JLabel) pane.getComponent(j);
            if(korttiLabel.isVisible())
                korttiLabel.setEnabled(false);
        }
    }
    private void poistaMaalausAlaspain(JLayeredPane pane, int i){
        for(int j = pane.getComponentCount() -1; j >= i; j--){
            JLabel korttiLabel = (javax.swing.JLabel) pane.getComponent(j);
            korttiLabel.setEnabled(true);
        }
    }
    
    private void paivitaTila(JLabel korttiLabel){
        if(guiSovellus.onkoKortti(korttiLabel.getName())){
            korttiLabel.setIcon(guiSovellus.getKortinKuva(korttiLabel.getName()));
            korttiLabel.setVisible(true);
        }
        else
            korttiLabel.setVisible(false);
    }
    
    private void paivitaTilaBorderilla(JLabel korttiLabel){
        if(guiSovellus.onkoKortti(korttiLabel.getName()))
            korttiLabel.setIcon(guiSovellus.getKortinKuva(korttiLabel.getName()));
        else
            korttiLabel.setIcon(guiSovellus.getTyhjaKortti());
        }
    
    public void alusta(GUISovellus sovellus){
        guiSovellus = sovellus;
        aktiivinen = null;
        aktiivistenMaaraJosUseampi = 0;
        komentoKirjasto = new KomentoKirjasto();
        paivita();
        this.setVisible(true);
    }
    public void paivita(){

        if(aktiivinen != null)
            tyhjennaAktiivinen();

        if(guiSovellus.onkoJakopakassaVaarinpainKortteja())
            jakopakka00.setIcon(new javax.swing.ImageIcon(getClass().getResource("/GUI/Images/BACK_0.png")));
        else
            jakopakka00.setIcon(new javax.swing.ImageIcon(getClass().getResource("/GUI/Images/EMPTYSLOT.png")));
        
        
        for(int i = jakopakka.getComponentCount() -1; i > 0; i--){
            javax.swing.JLabel korttiLabel = (javax.swing.JLabel) jakopakka.getComponent(i);
            paivitaTila(korttiLabel);
        }
        for(int i = maalipakat.getComponentCount() -1; i >= 0; i--){
            javax.swing.JLabel korttiLabel = (javax.swing.JLabel) maalipakat.getComponent(i);
            paivitaTilaBorderilla(korttiLabel);
        }
        for(int i = slotti0.getComponentCount() -1; i >= 0; i--){
            javax.swing.JLabel korttiLabel = (javax.swing.JLabel) slotti0.getComponent(i);
            paivitaTila(korttiLabel);
        }
        for(int i = slotti1.getComponentCount() -1; i >= 0; i--){
            javax.swing.JLabel korttiLabel = (javax.swing.JLabel) slotti1.getComponent(i);
            
            paivitaTila(korttiLabel);
        }
        for(int i = slotti2.getComponentCount() -1; i >= 0; i--){
            javax.swing.JLabel korttiLabel = (javax.swing.JLabel) slotti2.getComponent(i);
            
            paivitaTila(korttiLabel);
        }
    }
